mapper.insert:
INSERT INTO
    price (
          price_id
        , eur
        , exchange_rate
        , krw
        , tool_id
        , quantity
    )
VALUES (
      #{priceId}
    , #{eur}
    , #{exchangeRate} 
    , #{krw} 
    , #{toolId}
    , #{quantity}
)

mapper.update:
UPDATE 
    price
SET
      eur = #{eur}
    , exchange_rate = #{exchangeRate} 
    , krw = #{krw} 
    , tool_id = #{toolId}
    , quantity = #{quantity}
WHERE
    price_id = #{priceId}
