mapper.insert:
INSERT INTO
    sender (
          spid_id
        , spid_dept
        , spid_name
        , spid_tel1
        , spid_tel2
        , spid_email
        , job
    )
VALUES (
      #{spidId}
    , #{spidDept}
    , #{spidName} 
    , #{spidTel1} 
    , #{spidTel2} 
    , #{spidEmail} 
    , #{job}
)

mapper.update:
UPDATE 
    sender
SET
      spid_dept = #{spidDept}
    , spid_name = #{spidName} 
    , spid_tel1 = #{spidTel1} 
    , spid_tel2 = #{spidTel2} 
    , spid_email = #{spidEmail} 
    , job = #{job}
WHERE
    spid_id = #{spidId}
