mapper.insert:
INSERT INTO
    vender (
          vender_id
        , vender_name
        , spid_id
    )
VALUES (
      #{venderId}
    , #{venderName}
    , #{spidId}
)

mapper.update:
UPDATE
    vender
SET
      vender_name = #{venderName}
    , spid_id = #{spidId}
WHERE  
    vender_id = #{venderId}
