mapper.insert:
INSERT INTO
    estimate (
          estimate_id
        , title
        , estimate_price
        , estimate_origin_price
        , spid_id
        , estimate_date
        , estimate_note
        , customer_id
    )
VALUES (
      #{estimateId}
    , #{title}
    , #{estimatePrice}
    , #{estimateOriginPrice}
    , #{spidId}
    , #{estimateDate}
    , #{estimateNote}
    , #{customerId}
)

INSERT INTO
    estimate_detail (
          estimate_detail_id
        , estimate_details
        , quantity
        , unit
        , unit_price
        , supply_price
        , estimate_id
        , discount_rate
        , start_maintenance
        , end_maintenance
        , tool_license
        , tool_name
    )
VALUES 

    (
          #{item.estimateDetailId}
        , #{item.estimateDetails}
        , #{item.quantity}
        , #{item.unit}
        , #{item.unitPrice}
        , #{item.supplyPrice}
        , #{item.estimateId}
        , #{item.discountRate}
        , #{item.startMaintenance}
        , #{item.endMaintenance}
        , #{item.toolLicense}
        , #{item.toolName}
    )

INSERT INTO
    customer (
          customer_id
        , customer_company
        , customer_name
        , customer_tel
        , customer_email    
    )
VALUES ( 
      #{customerId}
    , #{customerCompany}
    , #{customerName}
    , #{customerTel}
    , #{customerEmail}
)

mapper.select:
SELECT estimate.estimate_id , title , estimate_price , estimate_origin_price , spid_id , estimate_date , estimate_note , customer_id , estimate_detail_id , estimate_details , quantity , unit , unit_price , supply_price , discount_rate , start_maintenance , end_maintenance , tool_name , tool_license FROM estimate, estimate_detail WHERE estimate.estimate_id = estimate_detail.estimate_id AND customer_id = #{customerId} AND (
tool_name LIKE CONCAT(#{item.toolName}, '%') AND tool_license = #{item.toolLicense}
)
